mapper.select:
SELECT id id, title title , type type , link_url url , logo_file_name thumbnail , logo_file_dir directory , specifications specifications, deleted deleted, created_time createTime , created_id createUser, modified_time updateTime , modified_id updateUser , end_time endTime, si.select_value state FROM m_advertising_info ai inner join m_select_info si on ai.publish = si.select_key
inner join m_select_info d on d.select_key = ai.type
where ai.deleted = 0 and si.type_code = 'publish_type' and d.select_value like concat(#{title} , '%') LIMIT #{start},#{pageSize}

mapper.insert:
insert into m_advertising_info( id , title , type , link_url , deleted , logo_file_name , logo_file_dir , specifications , created_time , created_id
, end_time
) values ( #{id} , #{title} , #{type} , #{url} , #{deleted} , #{thumbnail} , #{directory} , #{specifications} , now() , #{createUser}
, #{endTime}
)

mapper.update:
update m_advertising_info set title = #{title}, type=#{type} , link_url = #{url} , specifications = #{specifications} , logo_file_name = #{thumbnail}, modified_id = #{updateUser}, modified_time = now()
, end_time = #{endTime}
where id = #{id}

