mapper.select:
select mi.id as creditManagerId, mi.real_name as personName, ci.half_img as personImg, mi.star_level as starLevel, oi.name as organization, concat(ap.name,ac.name) as serverZone, mi.good_business as goodBusiness, count(di.id) as applyCount, case when ci.id is null then 0 else 1 end as realNameStatus, oi.id as organizationId from c_manager_info mi left join c_certification_info ci ON ci.userid = mi.id and ci.deleted = 0 left join m_organization_info oi ON oi.number = mi.organization left join m_area_city ac ON ac.code = mi.city left join m_area_province ap ON ap.code = mi.province left join m_order_info di ON di.blank_audite_person = mi.id where 1=1
and mi.good_business like concat(concat('%', #{goodBusiness}),'%')
and mi.city = #{serverZone}
and oi.genre = #{organizationType}
and ci.userid is not null
and mi.id= #{managerId}
group by mi.id desc
order by mi.last_login_time desc
order by mi.star_level desc
limit #{index},10
;

select count(*) from (select count(mi.id) from c_manager_info mi left join c_certification_info ci ON ci.userid = mi.id and ci.deleted = 0 left join m_organization_info oi ON oi.number = mi.organization where 1=1
and mi.good_business like concat(concat('%', #{goodBusiness}),'%')
and mi.city = #{serverZone}
and oi.genre = #{organizationType}
and ci.userid is not null
and mi.id= #{managerId}
group by mi.id desc) t;

select mi.id as id, mi.real_name as realName, case mi.sex when 1 then '男' when 2 then '女' when 3 then '保密' end as sex, mi.tel_number as tel, mi.email as email, mi.star_level as star, ap.name as province, ac.name as city, oi.name as organization, count(di.id) as customerAmount, count(sc.id) as successCaseAmount, ro.remaining_sum as moneyRemaind, mi.status as status, date_format(mi.created_time, '%Y-%m-%d') as registerDate from c_manager_info mi left join m_area_province ap ON ap.code = mi.province left join m_area_city ac ON ac.code = mi.city left join m_organization_info oi ON oi.id = mi.organization left join m_order_info di ON di.blank_audite_person = mi.id left join c_success_case sc ON sc.credit_manager_id = mi.id left join m_reward_outline ro ON ro.user_id = mi.id where mi.deleted = 0
group by mi.id
limit #{index},10 ;

update.foreach:
update c_manager_info 
set 
    status = #{status}
where
    id = #{id}

mapper.select:
select name as applicant, level as level, date_format(evaluation_time,'%Y-%m-%d %H:%i') as evaluationTime, evaluate_content as evaluateContent from m_order_info where credit_manager_id = #{managerId} and deleted = 0
and level = #{star}
limit #{index},10;
