mapper.select:
select m.id, m.company, m.code, u.name receiver, p.name area_province, c.name area_city, z.name area_zone, m.contact_name, m.order_type, m.order_status, m.deleted, s.select_value as `status`, m.credit, m.contact_phone, m.created_id, m.created_time, m.modified_id, m.modified_time, m.remark from m_product_fast_info m left join m_area_province p on m.area_province = p.id left join m_area_city c on m.area_city = c.id left join m_area_zone z on m.area_zone = z.id left join m_select_info s on s.select_key = m.status and type_code = #{typeCode} left join m_user_info u on u.id = m.receiver where m.deleted = 0
and m.code like '%' #{fspdt.code} '%'
and company like '%' #{fspdt.company} '%'
and contact_name like '%' #{fspdt.contactName} '%'
and contact_phone like '%' #{fspdt.contactPhone} '%'
and m.status = #{fspdt.status}
and m.order_type = #{fspdt.orderType}
and m.order_status = #{fspdt.orderStatus}
limit #{pageNo},#{pageSize}

mapper.update:
update m_product_fast_info set `status` = #{fspdt.status}, modified_id = #{name}, modified_time = now()
,tax_audite_time = now(), tax_audite_person = #{fspdt.taxAuditePerson}, tax_audite_remark = #{fspdt.taxAuditeRemark}
,tax_audite_status = 1
,tax_audite_status = 0
,blank_audite_time = now(), blank_audite_person = #{fspdt.blankAuditePerson}, blank_audite_remark = #{fspdt.blankAuditeRemark}
,blank_audite_status = 1
,blank_audite_status = 0
,credit_real = #{fspdt.creditReal}, receive_time =now(), remark= #{fspdt.remark}, receiver = #{name}
where id = #{fspdt.id}
