mapper.update:
update ${tabName}
 set deleted = 1,
 	 modified_time = now(),
	 modified_id = #{modifiedId}
where order_id = #{orderId} and type = #{type}

mapper.select:
select a.*, o.code as code, s.select_value as `status`, d.`name` as product_name from c_order_appoint a left join m_order_info o on o.id = a.order_id left join m_product_info d on d.id = o.product_info left join m_select_info s on s.select_key = o.status and s.type_code = 'order_status' where a.deleted = 0 and a.type = '1' and o.deleted = 0 and o.order_type = '4' and o.order_status != '1' and o.status in (6,7,8,9,10,11) and a.manage_id = #{manageId}
and o.status = #{search.status}
and a.applicant_time > #{search.startTime}
and a.applicant_time < #{search.endTime}
and o.code like '%' #{search.code} '%'
and a.applicant_phone like '%' #{search.applicantPhone} '%'
and a.applicant_name like '%' #{search.applicantName} '%'
and o.product_info = #{search.product}
union all
select a.*, f.code as code, s.select_value as `status`, null as product_name from c_order_appoint a left join m_product_fast_info f on f.id = a.order_id left join m_select_info s on s.select_key = f.status and s.type_code = 'order_status' where a.deleted = 0 and a.type = '2' and f.deleted = 0 and f.order_type = '4' and f.order_status != '1' and f.status in (6,7,8,9,10,11) and a.manage_id = #{manageId}
and f.status = #{search.status}
and a.applicant_time > #{search.startTime}
and a.applicant_time < #{search.endTime}
and f.code like '%' #{search.code} '%'
and a.applicant_phone like '%' #{search.applicantPhone} '%'
and a.applicant_name like '%' #{search.applicantName} '%'
limit #{pageNo}, #{pageSize}

select count(*) from c_order_appoint a left join m_order_info o on o.id = a.order_id and o.deleted = 0 left join m_product_fast_info f on f.id = a.order_id and f.deleted = 0 where a.deleted = 0 AND (f.order_type = '4' AND f.order_status = '4' AND f.`status` in(6,7,8,9,10,11)) OR ( o.order_type = '4' AND o.order_status = '4' AND o.`status` IN (6,7,8,9,10,11)) and a.manage_id = #{manageId}
and (o.status = #{search.status} || f.status = #{search.status})
and a.applicant_time > #{search.startTime}
and a.applicant_time < #{search.endTime}
and o.code like '%' #{search.code} '%'
and a.applicant_phone like '%' #{search.applicantPhone} '%'
and a.applicant_name like '%' #{search.applicantName} '%'
and o.product_info = #{search.product}

select a.id, o.order_type as type, o.code as code, a.applicant_name as applicant_name, d.`name` as product_name, a.applicant_phone as applicant_phone, a.credit as credit, a.limit_date as limit_date, a.company_name as company_name, a.applicant_time as applicant_time, s1.select_value as order_type, s.select_value as `status` from c_order_appoint a left join m_order_info o on o.id = a.order_id and o.deleted = 0 left join m_product_info d on d.id = o.product_info left join m_select_info s on s.select_key = o.status and s.type_code = 'order_status' left join m_select_info s1 on s1.select_key = o.order_type and s1.type_code = 'order_type' where a.deleted = 0 and a.type = '1' and o.order_type = '4' and o.order_status = '1' and a.manage_id = #{manageId}
union all select a.id, o.order_type as type, o.code as code, ap.name as applicant_name, d.`name` as product_name, ap.phone as applicant_phone, a.credit as credit, a.limit_date as limit_date, l.company_name as company_name, o.created_time as applicant_time, s1.select_value as order_type, s.select_value as `status` from c_order_fixed a left join m_order_info o on o.id = a.order_id and o.deleted = 0 left join m_product_info d on d.id = o.product_info left join m_license_info l on o.license_info = l.id left join m_applicant_info ap on ap.id = o.applicant_info left join m_select_info s on s.select_key = o.status and s.type_code = 'order_status' left join m_select_info s1 on s1.select_key = o.order_type and s1.type_code = 'order_type' where a.deleted = 0 and a.type = '1' and o.order_type = '1' and o.order_status = '1' and a.manage_id = #{manageId}
union all select a.id, o.order_type as type, o.code as code, a.applicant_name as applicant_name, p.name as product_name, a.applicant_phone as applicant_phone, a.credit as credit, a.limit_date as limit_date, a.company_name as company_name, a.applicant_time as applicant_time, s1.select_value as order_type, s.select_value as `status` from c_order_appoint a left join m_product_fast_info o on o.id = a.order_id and o.deleted = 0 left join m_select_info s on s.select_key = o.status and s.type_code = 'order_status' left join m_select_info s1 on s1.select_key = o.order_type and s1.type_code = 'order_type' left join m_product_info p on p.id = o.product_id where a.deleted = 0 and a.type = '2' and o.order_type = '4' and o.order_status = '1' and a.manage_id = #{manageId}
union all select a.id, o.order_type as type, o.code as code, o.contact_name as applicant_name, p.name as product_name, o.contact_phone as applicant_phone, a.credit as credit, a.limit_date as limit_date, o.company as company_name, o.created_time as applicant_time, s1.select_value as order_type, s.select_value as `status` from c_order_fixed a left join m_product_fast_info o on o.id = a.order_id and o.deleted = 0 left join m_select_info s on s.select_key = o.status and s.type_code = 'order_status' left join m_select_info s1 on s1.select_key = o.order_type and s1.type_code = 'order_type' left join m_product_info p on p.id = o.product_id where a.deleted = 0 and a.type = '2' and o.order_type = '1' and o.order_status = '1' and a.manage_id = #{manageId}
limit #{pageNo}, #{pageSize}

select ((select count(*)
from 
	c_order_appoint a
left join m_order_info     o   on o.id = a.order_id  and o.deleted = 0 



where a.deleted = 0 and a.type = '1' and o.order_type = '4' and o.order_status = '1'
and a.manage_id = #{manageId}
)
+(select count(*)
from
	c_order_fixed a
left join m_order_info     o   on o.id = a.order_id  and o.deleted = 0 


left join m_applicant_info ap  on ap.id = o.applicant_info 


where a.deleted = 0 and a.type = '1' and o.order_type = '1' and o.order_status = '1'
and a.manage_id = #{manageId}
)

	+(select count(*)
	from c_order_appoint a
	left join m_product_fast_info     o   on o.id = a.order_id  and o.deleted = 0


	where a.deleted = 0 and a.type = '2'  and o.order_type = '4' and o.order_status = '1'
	and a.manage_id = #{manageId}	
	)
	+(select count(*)
	from c_order_fixed a
	left join m_product_fast_info     o   on o.id = a.order_id  and o.deleted = 0 


	where a.deleted = 0 and a.type = '2' and o.order_type = '1' and o.order_status = '1'
	and a.manage_id = #{manageId}	
	)

)
as num
