mapper.select:
SELECT o.id, applicant, b.name as bank, d.`name` as product_name, o.name, link_user_name, order_number, o.credit, o.order_type, o.order_status, link_phone, d.code as product_code, o.code, commit_time, s.select_value as status, audite_time, audite_person, applicat_person, company_info, blank_audite_time, blank_audite_remark, blank_audite_status, blank_audite_person, tax_audite_remark, tax_audite_time, tax_audite_status, tax_audite_person, o.created_time, p.`name` AS province, c.`name` AS city, z.`name` AS zone FROM m_order_info o LEFT JOIN m_applicant_info m ON o.applicant_info = m.id LEFT JOIN m_area_province p ON m.area_province = p.id LEFT JOIN m_area_city c ON m.area_city = c.id LEFT JOIN m_area_zone z ON m.area_zone = z.id LEFT JOIN m_select_info s ON s.select_key = o.`status` AND s.type_code = #{typeCode} LEFT JOIN m_bank_info b ON o.bank = b.id LEFT JOIN m_product_info d ON d.id = o.product_info where o.deleted = 0
and o.code like '%' #{order.code} '%'
and company_info like '%' #{order.companyInfo} '%'
and applicat_person like '%' #{order.applicatPerson} '%'
and link_phone like '%' #{order.linkPhone} '%'
and o.status = #{order.status}
and o.order_type = #{order.orderType}
and o.order_status = #{order.orderStatus}
limit #{pageNo}, #{pageSize}

SELECT o.id, applicant, b.name as bank, d.`name` as product_name, o.name, link_user_name, order_number, o.credit, o.order_type, o.order_status, link_phone, d.code as product_code, o.code, commit_time, s.select_value as status, audite_time, audite_person, applicat_person, company_info, blank_audite_time, blank_audite_remark, blank_audite_status, blank_audite_person, tax_audite_remark, tax_audite_time, tax_audite_status, tax_audite_person, o.created_time, p.`name` AS province, c.`name` AS city, z.`name` AS zone FROM m_order_info o LEFT JOIN m_applicant_info m ON o.applicant_info = m.id LEFT JOIN m_area_province p ON m.area_province = p.id LEFT JOIN m_area_city c ON m.area_city = c.id LEFT JOIN m_area_zone z ON m.area_zone = z.id LEFT JOIN m_select_info s ON s.select_key = o.`status` AND s.type_code = #{typeCode} LEFT JOIN m_bank_info b ON o.bank = b.id LEFT JOIN m_product_info d ON d.id = o.product_info where o.deleted = 0
and o.code like '%' #{order.code} '%'
and company_info like '%' #{order.companyInfo} '%'
and applicat_person like '%' #{order.applicatPerson} '%'
and link_phone like '%' #{order.linkPhone} '%'
and o.status = #{order.status}
and o.order_type = #{order.orderType}
and o.status in #{id}
limit #{pageNo}, #{pageSize}

	select o.id,
		   o.code,
		   d.`name` as product_name,
		   b.name as bank,
		   o.created_time,
		   o.credit,
		   s.select_value as status

	from m_order_info o
	LEFT JOIN m_select_info    s ON s.select_key = o.`status` AND s.type_code = #{typeCode}
	LEFT JOIN m_bank_info      b ON o.bank = b.id
	LEFT JOIN m_product_info   d ON d.id = o.product_info
	where o.deleted != 1 and o.member_id = #{id}








				and o.status in (3,4)


				and o.status in (6,7)


				and o.status = #{order.status}





		and o.created_time >= #{order.startTime}



     	and o.created_time <= #{order.endTime}




			order by o.created_time desc


			order by o.created_time asc



	limit #{pageNo}, #{pageSize}

	select count(*)
		from m_order_info o
	where deleted != 1 and member_id = #{id}




				and o.status in (1,2)



				and o.status in (3,4)


				and o.status in (6,7)


				and o.status = #{order.status}





		and o.created_time >= #{order.startTime}



     	and o.created_time <= #{order.endTime}



		order by o.created_time desc

mapper.update:
update m_order_info set status = #{order.status}, modified_time = now(), modified_id = #{order.modifiedId}
,tax_audite_time = now(), tax_audite_person = #{order.taxAuditePerson}, tax_audite_remark = #{order.taxAuditeRemark}
,tax_audite_status = 1
,tax_audite_status = 0
,blank_audite_time = now(), blank_audite_person = #{order.blankAuditePerson}, blank_audite_remark = #{order.blankAuditeRemark}
,blank_audite_status = 1
,blank_audite_status = 0
,credit_real = #{order.creditReal},
audite_time =now(),
audite_time = #{order.auditeTime},
remark= #{order.remark}
where id = #{order.id}

update m_order_info
	set modified_time = now(),
		modified_id = #{modifiedId},
		status = #{status}
where id = 

	#{id}

