mapper.select:
select oi.id as id, oi.code as orderId, ai.name as applicant, pi.name as productName, ai.phone as applicantTel, ai.amount as loanAmount, ai.deadline as loanLimit, li.company_name as company, date_format(oi.created_time,'%Y-%m-%d') as applicantDate, s1.select_value as orderStatus, s2.select_value as orderType from m_order_info oi, m_product_info pi, m_applicant_info ai, m_license_info li, (select * from m_select_info where type_code = 'order_member_status') s1, (select * from m_select_info where type_code = 'order_type') s2 where oi.product_info = pi.id and oi.applicant_info = ai.id and li.id = oi.license_info and oi.status = s1.select_key and oi.order_type = s2.select_key and oi.credit_manager_id = #{managerId}
and oi.created_time >= #{startTime}
and oi.created_time <= #{endTime}
and pi.id = #{product}
and ai.phone like concat(concat('%',#{applicantPhone}),'%')
and ai.name like concat(concat('%',#{applicantName}),'%')
and oi.status = #{status}
and oi.order_type = #{orderType}
order by oi.created_time desc limit #{index} , 8;

select 
    count(oi.id)
from
    m_order_info oi,
    m_product_info pi,
    m_applicant_info ai,
    m_license_info li
where
    oi.product_info = pi.id
        and oi.applicant_info = ai.id
        and li.id = oi.license_info
        and oi.credit_manager_id = #{managerId}

        and oi.created_time >= #{startTime}


        and oi.created_time <= #{endTime}


        and pi.id = #{product}


        and ai.phone like concat(concat('%',#{applicantPhone}),'%')


        and ai.name like concat(concat('%',#{applicantName}),'%')


        and oi.status = #{status}


        and oi.order_type = #{orderType}
