mapper.select:
select
	count(sc.id)
from
	c_success_case sc,
    m_product_info pi
where
	sc.product_id = pi.id
	and sc.credit_manager_id=#{managerId}

	and pi.name like concat(concat('%', #{productName}),'%')


	and sc.loan_amount >= #{amountStart}


	and sc.loan_amount <= #{amountEnd}


	and sc.loan_date >= #{dateStart}


	and sc.loan_date <= #{dateEnd}

select sc.id as id, pi.id as productId, pi.name as productName, sc.applicant_company as applicantCompany, si.select_value as loanType, sc.loan_amount as loanAmount, sc.loan_date as loanDate, sc.loan_period as loanPeriod, sc.loan_days as loanDays, ap.name as loanProvince, ac.name as loanCity, sc.month_interest as monthInterest from c_success_case sc, m_product_info pi, m_area_province ap, m_area_city ac, (select * from m_select_info where type_code = 'loan_type') si where sc.product_id = pi.id and sc.loan_type = si.select_key and sc.loan_province = ap.code and sc.loan_city = ac.code
and pi.name like concat(concat('%', #{productName}),'%')
and sc.loan_amount >= #{amountStart}
and sc.loan_amount <= #{amountEnd}
and sc.loan_date >= #{dateStart}
and sc.loan_date <= #{dateEnd}
and sc.credit_manager_id = #{managerId}
order by sc.order_number limit #{index} , 10;
