mapper.select:
SELECT id as id,title as title,DATE_FORMAT(spread_time,'%Y-%m-%d') as date, type ,
	   help_status as status,content as content,author as author
FROM m_assist_info WHERE deleted=0 AND id=#{code}

mapper.insert:
insert into 
	m_help_type(name,order_num , modified_time , created_time , modified_id , created_id)
values (#{name},#{orderNum} , now() , now() , #{createName} , #{createName});

mapper.select:
select id as code, title as name, content, type, author, help_status as status, date_format(spread_time,'%Y-%m-%d') as date from m_assist_info where
deleted = 0 and help_status = 2
order by order_num;
